function show1() {
document.querySelectorAll(".cards").forEach((card) => {
  card.addEventListener("click", (event) => {
    event.currentTarget.classList.toggle("#plongee");
  });
});
};

export {show1};


function hide() {
  const button = document.querySelectorAll(".diving");

  const press = document.querySelectorById('plongee').style.display = 'none';


  button.onclick = () => {

      if(press == true){
          document.querySelectorById('plongee').style.display = "";
      }
      else {
          document.querySelectorById('plongee').style.display = 'none';
      }
      press = !press;
  };
};
export {hide};


function showHide() {
  const button = document.querySelector('.diving');
  button.addEventListener('click', (event) => {
    console.log(event);
    event.currentTarget.classList.toggle("#plongee");
  });
};
export {showHide};
